INSERT INTO `goods_info` VALUES (22, '寻梦环游记', '这是一部童话电影，充满着虚幻以及美好，讲述了热爱音乐的小男孩米格和落魄乐手埃克托在五彩斑斓的神秘世界开启了一段奇妙冒险旅程的故事。', 4231, '否', '安东尼·冈萨雷斯、本杰明·布拉特', '2017-11-24 00:00:00', '105', 7, '[24]');
